import axios from "axios";
import { useEffect, useReducer, useState } from "react";
import Speaker from "../Speaker";
import SpeakerSearchBar from "../SpeakerSearchBar";

const Speakers = () => {
    const REQUEST_STATUS = {
        LOADING: "loading",
        SUCCESS: "success",
        ERROR: "error",
    };

    const reducer = (state, action) => {
        switch (action.type) {
            case "GET_ALL_SUCCESS":
                return {
                    ...state,
                    status: REQUEST_STATUS.SUCCESS,
                    speakers: action.speakers,
                };
            case "UPDATE_STATUS":
                return { ...state, status: action.status };
        }
    };

    const [{ speakers, status }, dispatch] = useReducer(reducer, {
        status: REQUEST_STATUS.LOADING,
        speakers: [],
    });

    const [error, setError] = useState({});

    const [searchQuery, setSearchQuery] = useState("");

    const isSuccess = status === REQUEST_STATUS.SUCCESS;
    const isError = status === REQUEST_STATUS.ERROR;
    const isLoading = status === REQUEST_STATUS.LOADING;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await axios.get(
                    "http://localhost:4000/speakers"
                );
                dispatch({ speakers: result.data, type: "GET_ALL_SUCCESS" });
            } catch (e) {
                dispatch({
                    status: REQUEST_STATUS.ERROR,
                    type: "UPDATE_STATUS",
                });
                setError(e);
            }
        };

        fetchData();
    }, []);

    const toggleFavSpeaker = (currentSpeaker) => {
        return { ...currentSpeaker, isFavorite: !currentSpeaker.isFavorite };
    };

    const onFavoriteToggleHandler = async (speaker) => {
        //1. Found the speaker, get the reference
        //2. Remove it
        //3. Put the new one at the same place.
        const index = speakers.indexOf(speaker);
        const newSpeaker = toggleFavSpeaker(speaker);

        try {
            await axios.put(
                `http://localhost:4000/speakers/${speaker.id}`,
                newSpeaker
            );

            dispatch([
                ...speakers.slice(0, index),
                newSpeaker,
                ...speakers.slice(index + 1),
            ]);
        } catch (e) {
            setStatus(REQUEST_STATUS.ERROR);
            setError(e);
        }
    };

    return (
        <div>
            <SpeakerSearchBar
                searchQuery={searchQuery}
                setSearchQuery={setSearchQuery}
            />

            {isLoading && <div>Loading...</div>}
            {isError && <div>There's an error! Message: {error.message} </div>}
            {isSuccess && (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 grid-cols-1 gap-12">
                    {speakers
                        .filter((speaker) => {
                            const targetStr = `${speaker.firstName} ${speaker.lastName}`.toLowerCase();
                            return searchQuery.length === 0
                                ? true
                                : targetStr.includes(searchQuery.toLowerCase());
                        })
                        .map((speaker) => (
                            <Speaker
                                {...speaker}
                                key={speaker.id}
                                onFavoriteToggle={() =>
                                    onFavoriteToggleHandler(speaker)
                                }
                            />
                        ))}
                </div>
            )}
        </div>
    );
};

export default Speakers;
