import react from 'react';

const Speakers = ({ speakers }) => {
    return (
        <div>
            {speakers.map(({ src, name }) => (
                <img src={src} alt={name} key={name} />
            ))}
        </div>
    );
};

const withData = (Component) => {
    const speakers = [
        { src: "images/speaker-component-1124.png", name: "Douglas Crockford" },
        { src: "images/speaker-component-1530.png", name: "Tamara Baker" },
        { src: "images/speaker-component-10803.png", name: "Eugene Chuvyrov" },
    ];

    return <Component speakers={speakers}></Component>;
};

export default withData(Speakers);
